import React from "react";

function Body(){
    // To print current date and time.
let currentDate = new Date()
currentDate = currentDate.getHours()
let greeting = '';
const style = {}
if(currentDate >=0  && currentDate <= 11){
    greeting = "Good Morning!";
    style.color = "green"
}else if(currentDate <= 16 && currentDate >= 12){
    greeting ="Good Afternoon!";
    style.color = "orchid"
}else if(currentDate > 4 && currentDate < 7){
    greeting = "Good Evening!";
    style.color = "blue"
}else if(currentDate >= 19 && currentDate <= 24){
    greeting = "Good Night";
    style.color = "red"
}

function Header(){
    return
    <> 
    <h1>Hello, <span style={style}> {greeting} </span> </h1>
    </>
}

}

export default Body;
